CommandUIHandler.CommandAction:
javascript:
                            var dialogOptions = {
                              url: '/_layouts/ApplyDocumentPrefixRibbon/DocPrefixHelp.aspx',
                              title: 'Apply Document Prefix Help',
                              allowMaximize: true,
                              showClose: true,
                              width:500,
                              height:400
                            };
                            SP.UI.ModalDialog.showModalDialog(dialogOptions);

javascript:
                            function dialogCallback(dialogResult, returnValue){
                              SP.UI.Notify.addNotification(returnValue);
                              SP.UI.ModalDialog.RefreshPage(SP.UI.DialogResult.OK);
                            }

                            var selectedItems = SP.ListOperation.Selection.getSelectedItems();
                            var selectedItemIds = '';
                            var selectedItemIndex;
                            for (selectedItemIndex in selectedItems){
                              selectedItemIds += '|' + selectedItems[selectedItemIndex].id;
                            }

                            var dialogOptions = {
                              url: '/_layouts/ApplyDocumentPrefixRibbon/DocPrefixPrompt.aspx?selectedItems=' +selectedItemIds +'&ListId=' +SP.ListOperation.Selection.getSelectedList(),
                              title: 'Set Document Prefix',
                              allowMaximize: false,
                              showClose: false,
                              width:500,
                              height:400,
                              dialogReturnValueCallback: dialogCallback
                            };

                            SP.UI.ModalDialog.showModalDialog(dialogOptions);

CommandUIHandler.EnabledScript:
javascript:
                            function checkIsEnabled(){
                              // check items selected
                              var selectedItems = SP.ListOperation.Selection.getSelectedItems();
                              var count = CountDictionary(selectedItems);
                              return (count > 0);
                            }; 

                            checkIsEnabled();
